import React, { useEffect, useState } from "react";
import axios from "axios";
import { 
    styled,
    Box,
    Grid,
    Paper,
} from "@mui/material";
import QuizItem from "../components/QuizItem";

const MainPage = styled(Box)(() => ({
    backgroundColor: '#1C0772',
    height: '100vh'
  }));

const MainWrapper = styled(Paper)(() => ({
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  maxWidth: '1000px',
  margin: '0 auto',
  boxSizing: 'border-box',
  backgroundColor: 'none',
  paddingTop: '150px',
}));

export default function UsersList() {
    let [courses, setCourses] = useState([]);
    // let [counter, setCounter] = useState(0);
    useEffect(() => {
        (async () => {
            const { data } = await axios.get("https://63f9f88abeec322c57eae484.mockapi.io/Courses");
            setCourses(data);
            console.log(data);
        })();
    }, []);

    // const handleSetCounter = () => {
    //     setCounter(++counter);
    //     console.log(counter);
    // };

    return (
        <MainPage>
            <MainWrapper sx={{ flexGrow: 1 }}>
                {/* <button onClick={handleSetCounter}>Set counter</button> */}
                <Grid container spacing={2}>
                    <Grid item xs={6} md={4}>
                        {courses.map((course, index) => (
                            <QuizItem 
                                key={index}
                                course={course.course}
                                descr={course.description}
                                img={course.avatar}
                                id={index}
                                margin='20px'
                            />
                        ))}   
                    </Grid>
                </Grid>
            </MainWrapper>
        </MainPage>
    )
}
