import React, { useEffect, useState } from 'react';
import { 
  styled,
} from '@mui/material';
import ProductCard from './CategoryItem';
import { productTypes } from '../api';

const CardsWrapper = styled('div')(() => ({
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'center',
    margin: '0 auto',
    maxWidth: '1280px',
    padding: '50px 0px',
}));
const CategoriesTitle = styled('div')(() => ({
    textAlign: 'center',
    fontSize: '3rem',
    fontWeight: 'bold',
    textTransform: 'uppercase',
    margin: '60px 0px 0px',
    color: '#ffffff',
}));

export default function ShopItems() {
    const [productsTypesData, setProduct] = useState([]);
  
      useEffect(() => {
          (async () => {
              try {
                  const { data } = await productTypes.fetch();
                  setProduct(data);
                  console.log(data);
              } catch (err) {
                  console.log(err);
              }
          })();
      });
  
    return (
        <>
            <CategoriesTitle>
                Popular categories
            </CategoriesTitle>
            <CardsWrapper>
                {productsTypesData.map(type => (
                    <ProductCard 
                        key={type.key}
                        productImage={type.typeImg}
                        productType={type.typeName}
                        productDescr={type.typeDescr}
                    />
                ))}
            </CardsWrapper>
        </>
    
    );
  }
