import React, { useState } from 'react';
import { 
  styled,
  Card,  
  CardMedia,  
  CardContent,  
  CardActions,   
  IconButton,  
  Typography, 
} from '@mui/material';
import FavoriteIcon from '@mui/icons-material/Favorite';
import PlayCircleOutlineIcon from '@mui/icons-material/PlayCircleOutline';
import InfoOutlinedIcon from '@mui/icons-material/InfoOutlined';

const QuizCard = styled(Card)(() => ({
  maxWidth: '380px',
  margin: '15px 20px',
  fontSize: '30px',
  borderRadius: '20px',
  cursor: 'pointer',
  backgroundColor: '#ffffff',
  color: '#ffffff',
  padding: '15px',
  transition: '0.5s',
  height: 'fit-content',
}));

const QuizCardHead = styled('div')(() => ({
  display: 'flex',
  marginBottom: '10px',
  alignItems: 'center',
  justifyContent: 'space-between',
}));

const QuizCardTitle = styled('div')(() => ({
  color: '#008EC9',
  fontSize: '20px',
  fontWeight: 'bold',
}));

const QuizAvatar = styled(CardMedia)(() => ({
  width: '45px',
  height: '45px',
  borderRadius: '50%',
  display: 'block',
}));

const QuizCardDescr = styled(Typography)(() => ({
  color: '#484554',
  fontSize: '16px',
  textAlign: 'center',
}));

export default function QuizItem({ course, id, descr, img, baner }) {

  const [open, setModalOpen] = useState(false);
  
  const handleModalOpen = () => setModalOpen(true);
  const handleClose = () => setModalOpen(false);

  return (
    <QuizCard sx={{ maxWidth: 345 }}>
      <QuizCardHead>
        <QuizAvatar
          component="img"
          image={img}
        />
        <QuizCardTitle>
          {course} quiz
        </QuizCardTitle>
      </QuizCardHead>
      <CardMedia
        component="img"
        height="194"
        image={baner}
        alt="Paella dish"
      />
      <CardContent>
        <QuizCardDescr>
          {descr}
        </QuizCardDescr>
      </CardContent>
      <CardActions disableSpacing>
        <IconButton 
        aria-label="add to favorites"
        open={open}
        onClose={handleClose}
        onClick={() => handleModalOpen}
        >
          <FavoriteIcon />
        </IconButton>
        <IconButton aria-label="info">
          <InfoOutlinedIcon />
        </IconButton>
        <PlayCircleOutlineIcon aria-label="start quiz"/>
      </CardActions>
    </QuizCard>
  );
}
