import React from "react";
import { 
    Typography,
    Button,
    CardMedia,
    CardContent,
    CardActions,
    Card,
} from "@mui/material";

export default function QuizItem({ course, id, handleButtonClick }) {
    console.log(course);
    return (
        <div className="quiz-page">
          <h2>Quizzes</h2>
          <div className="quiz-cards">
            <Card sx={{ maxWidth: 345 }}>
              <CardMedia
                component="img"
                height="140"
                image="/assets/js-quiz.jpg"
                alt="JavaScript Quiz"
              />
              <CardContent>
                <Typography gutterBottom variant="h5" component="div">
                  {course}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Test your knowledge of JavaScript with this quiz.
                </Typography>
              </CardContent>
              <CardActions>
                <Button size="small">Start Quiz</Button>
                <Button size="small">Show More</Button>
              </CardActions>
            </Card>
            {/* More quiz cards go here */}
          </div>
        </div>
      );
}
