import React, { useState } from 'react';
import { 
    styled,
    useTheme,
    Box,  
    Drawer,  
    CssBaseline,  
    Toolbar,  
    List,  
    Divider,  
    IconButton,  
    ListItem,  
    ListItemButton, 
    ListItemIcon, 
    ListItemText 
} from '@mui/material';
//icons-material не импортятся скопом, выдает ошибку

import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import AddIcon from '@mui/icons-material/Add';
import FavoriteIcon from '@mui/icons-material/Favorite';

const DrawerHeader = styled('div')(() => ({
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'flex-end',
}));



export default function PersistentDrawerLeft({ handleDrawerOpen, handleDrawerClose}) {
  const theme = useTheme();
  

  return (
    <Box sx={{ display: 'flex' }}>
      <CssBaseline />
      
        <Toolbar>
          
        </Toolbar>
      
      <Drawer
        sx={{
          width: 240,
          flexShrink: 0,
          '& .MuiDrawer-paper': {
            width: 240,
            boxSizing: 'border-box',
            backgroundColor: 'black',
          },
        }}
        variant="persistent"
        anchor="left"
        open={open}
      >
        <DrawerHeader>
          <IconButton onClick={handleDrawerClose}>
            {theme.direction === 'ltr' ? <ChevronLeftIcon sx={{ color: 'white' }}/> : <ChevronRightIcon sx={{ color: 'white' }}/>}
          </IconButton>
        </DrawerHeader>
        <Divider sx={{ backgroundColor: 'white' }}/>
        <List>
          {['Add product', 'Favourites'].map((text, index) => (
            <ListItem key={text} disablePadding>
              <ListItemButton>
                <ListItemIcon>
                  {index % 2 === 0 ? <AddIcon sx={{ color: 'white' }}/> : <FavoriteIcon sx={{ color: 'white' }}/>}
                </ListItemIcon>
                <ListItemText primary={text} sx={{ color: 'white' }}/>
              </ListItemButton>
            </ListItem>
          ))}
        </List>
      </Drawer>
    </Box>
  );
}
