import React from 'react';
import { 
    styled, 
    Dialog, 
    DialogActions, 
    DialogContent, 
    DialogContentText, 
    DialogTitle,
    CardMedia,
} from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';

const ModalImg = styled(CardMedia)(() => ({
    width: '80%',
    height: '170px',
    margin: '0 auto',
    backgroundPosition: 'center',
    backgroundSize: 'contain',
}));

const ModalTitle = styled(DialogTitle)(() => ({
    color: '#FF6C00',
    fontWeight: 'bold',
    textAlign: 'center',
    position: 'relative',
    fontSize: '25px',
}));

const CloseModal = styled(CloseIcon)(() => ({
    position: 'absolute',
    top: '5px',
    right: '5px',
    cursor: 'pointer',
    color: '#000000',
    fontSize: '25px',
}));

export default function InfoDialog({ 
    open, handleOnClose, productImage, productType, productDescr
}) {

  return (
    <div>
      <Dialog
        open={open}
        onClose={handleOnClose}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <ModalTitle id="alert-dialog-title">
          {productType}
        </ModalTitle>
        <ModalImg
          image={productImage}
          title={productDescr}
        />
        <DialogContent>
          <DialogContentText id="alert-dialog-description">
          {productDescr}
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <CloseModal onClick={handleOnClose}>Close</CloseModal>
        </DialogActions>
      </Dialog>
    </div>
  );
}
