import React, { useState } from 'react';
import { 
    styled,
    Card,  
    CardActions,  
    CardContent,  
    CardMedia,  
    Button,  
    Typography,
    IconButton,  
} from '@mui/material';
import FavoriteIcon from '@mui/icons-material/Favorite';
import InfoDialog from './Modals/InfoModal';

const BikeCard = styled(Card)(() => ({
  backgroundColor: '#1E1D1B',
  webkitBoxShadow: '5px 5px 17px -7px rgba(0,0,0,0.75)',
  mozBoxShadow: '5px 5px 17px -7px rgba(0,0,0,0.75)',
  boxShadow: '5px 5px 17px -7px rgba(0,0,0,0.75)',
  '&:hover': {
    webkitBoxShadow: '0px 0px 4px 3px rgba(255,120,0,1)',
    mozBoxShadow: '0px 0px 4px 3px rgba(255,120,0,1)',
    boxShadow: '0px 0px 4px 3px rgba(255,120,0,1)',
  },
  borderRadius: '20px',
  margin: '10px 10px',
  textAlign: 'center',
  transition: '0.5',
  position: 'relative',
}));

const CardTypeTitle = styled(Typography)(() => ({
  color: '#FF9A40	',
  fontSize: '26px',
}));

const CardTypeDescr = styled(Typography)(() => ({
  color: '#ffffff',
  fontSize: '18px',
}));

const FavouriteButton = styled(IconButton)(() => ({
  position: 'absolute',
  top: '0',
  left: '1px',
}));

const ButtonsWrap = styled(CardActions)(() => ({
  display: 'flex',
  justifyContent: 'center',
  marginBottom: '10px',
}));

const CardButton = styled(Button)(() => ({
  backgroundColor: '#FFAE73',
  color: '#000000',
  '&:hover': {
    backgroundColor: '#A64600',
    color: '#ffffff',
  },
}));

export default function ProductCard({ 
  productImage, productType, productDescr
}) {
  const [open, setOpenModal] = useState(false);
    
  return (
    <>
      <BikeCard sx={{ width: 300 }}>
        <CardMedia
          sx={{ height: 170 }}
          image={productImage}
          title="green iguana"
        />
        <CardContent>
          <FavouriteButton aria-label="add to favorites">
            <FavoriteIcon />
          </FavouriteButton>
          <CardTypeTitle gutterBottom variant="h5" component="div">
            {productType}
          </CardTypeTitle>
          <CardTypeDescr gutterBottom variant="h2" component="div">
            {productDescr.slice(0, 80)}...
          </CardTypeDescr>
        </CardContent>
        <ButtonsWrap>
          <CardButton size="big" onClick={() => setOpenModal(true)}>More Info</CardButton>
          <CardButton size="big" >See models</CardButton>
        </ButtonsWrap>
      </BikeCard>
      <InfoDialog
        open={open}
        handleOnClose={() => setOpenModal(false)}
        productType={productType}
        productImage={productImage}
        productDescr={productDescr}
      />
    </>
  );
}
