import React from 'react';
import { 
  styled,
  Card,  
  CardMedia,  
  CardContent,  
  CardActions,   
  IconButton,  
  Typography, 
} from '@mui/material';
import FavoriteIcon from '@mui/icons-material/Favorite';
import PlayCircleOutlineIcon from '@mui/icons-material/PlayCircleOutline';
import InfoOutlinedIcon from '@mui/icons-material/InfoOutlined';

const QuizCard = styled(Card)(() => ({
  maxWidth: '380px',
  margin: '15px 20px',
  fontSize: '30px',
  borderRadius: '20px',
  cursor: 'pointer',
  backgroundColor: '#ffffff',
  color: '#ffffff',
  padding: '15px',
  transition: '0.5s',
  height: 'fit-content'
}));

const QuizCardHead = styled('div')(() => ({
  display: 'flex',
  marginBottom: '10px',
  alignItems: 'center',
  justifyContent: 'space-between',
}));

const QuizCardTitle = styled('div')(() => ({
  color: '#008EC9',
  fontSize: '20px',
  fontWeight: 'bold',
}));

const QuizAvatar = styled(CardMedia)(() => ({
  width: '45px',
  height: '45px',
  borderRadius: '50%',
  display: 'block',
}));

export default function QuizItem({ course, id, descr, img }) {


  const ExpandMore = styled((props) => {
    const { expand, ...other } = props;
    return <IconButton {...other} />;
  })(({ theme, expand }) => ({
    transform: !expand ? 'rotate(0deg)' : 'rotate(180deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  }));

  // const [modalOpen, setModalOpen] = useState(false);
  
  // const handleModalOpen = () => setModalOpen(true);
  // const handleClose = () => setModalOpen(false);

  return (
    <QuizCard sx={{ maxWidth: 345 }}>
      <QuizCardHead>
        <QuizAvatar
          component="img"
          image={img}
        />
        <QuizCardTitle>
          {course} quiz
        </QuizCardTitle>
      </QuizCardHead>
      <CardMedia
        component="img"
        height="194"
        image="https://www.elegantthemes.com/blog/wp-content/uploads/2018/04/Best-Code-and-Text-Editors.png"
        alt="Paella dish"
      />
      <CardContent>
        <Typography variant="body2" color="text.secondary">
          {descr}
        </Typography>
      </CardContent>
      <CardActions disableSpacing>
        <IconButton aria-label="add to favorites">
          <FavoriteIcon />
        </IconButton>
        <IconButton aria-label="info">
          <InfoOutlinedIcon />
        </IconButton>
        <ExpandMore>
          <PlayCircleOutlineIcon aria-label="start quiz"/>
        </ExpandMore>
      </CardActions>
    </QuizCard>
  );
}
