import React, { useEffect, useState } from "react";
import axios from "axios";
import { 
    styled,
    Box,
    Modal,
} from "@mui/material";
import QuizItem from "../components/QuizItem";
import TransitionsModal from "./Modals";


const MainWrapper = styled(Box)(() => ({
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  width: '100%',
  height: 'auto',
  boxSizing: 'border-box',
  backgroundColor: 'none',
  padding: '10px',
}));

export default function UsersList() {
    let [courses, setCourses] = useState([]);
    let [counter, setCounter] = useState(0);
    useEffect(() => {
        (async () => {
            const { data } = await axios.get("https://63f9f88abeec322c57eae484.mockapi.io/Courses");
            setCourses(data);
            console.log(data);
        })();
    }, []);

    const handleSetCounter = () => {
        setCounter(++counter);
        console.log(counter);
    };

    const [open, setOpen] = React.useState(false);
    const handleOpen = () => setOpen(true);
    const handleClose = () => setOpen(false);

    return (
        <MainWrapper>
            {/* <button onClick={handleSetCounter}>Set counter</button> */}
            {courses.map((course, index) => (
                <QuizItem
                    key={index}
                    course={course.course}
                    descr={course.description}
                    img={course.avatar}
                    id={index}
                    handleButtonClick={handleOpen}
                />
            ))}
        </MainWrapper>
    )
}
