import React from 'react';
import { 
  styled,
  Card,  
  CardHeader,  
  CardMedia,  
  CardContent,  
  CardActions,  
  Avatar,  
  IconButton,  
  Typography, 
} from '@mui/material';
import FavoriteIcon from '@mui/icons-material/Favorite';
import ShareIcon from '@mui/icons-material/Share';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';

const QuizCard = styled(Card)(() => ({
  maxWidth: '380px',
  margin: '0 20px',
  fontSize: '30px',
  borderRadius: '20px',
  cursor: 'pointer',
  backgroundColor: '#ffffff',
  color: '#ffffff',
  padding: '15px',
  transition: '0.5s',
  height: 'fit-content'
}));

const QuizAvatar = styled(Avatar)(() => ({
  width: '45px',
  height: '45px',
  borderRadius: '50%',
  display: 'block',
}));

export default function QuizItem({ course, id, descr, img }) {


  const ExpandMore = styled((props) => {
    const { expand, ...other } = props;
    return <IconButton {...other} />;
  })(({ theme, expand }) => ({
    transform: !expand ? 'rotate(0deg)' : 'rotate(180deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  }));

  // const [modalOpen, setModalOpen] = useState(false);
  
  // const handleModalOpen = () => setModalOpen(true);
  // const handleClose = () => setModalOpen(false);

  return (
    <QuizCard sx={{ maxWidth: 345 }}>
      <CardHeader sx={{ height: '40px' }}>
        <QuizAvatar>
          {img}
        </QuizAvatar>
        <Typography>
          {descr}
        </Typography>
      </CardHeader>
      <CardMedia
        component="img"
        height="194"
        image="https://www.elegantthemes.com/blog/wp-content/uploads/2018/04/Best-Code-and-Text-Editors.png"
        alt="Paella dish"
      />
      <CardContent>
        <Typography variant="body2" color="text.secondary">
          {course}
        </Typography>
      </CardContent>
      <CardActions disableSpacing>
        <IconButton aria-label="add to favorites">
          <FavoriteIcon />
        </IconButton>
        <IconButton aria-label="share">
          <ShareIcon />
        </IconButton>
        <ExpandMore>
          <ExpandMoreIcon />
        </ExpandMore>
      </CardActions>
    </QuizCard>
  );
}
