import React from "react";
import { 
    Typography,
    Button,
    CardMedia,
    CardContent,
    CardActions,
    Card,
    Modal,
    Box,
} from "@mui/material";
import TransitionsModal from "./Modals";

export default function QuizItem({ course, id, descr, img, handleOpen }) {

    

    return (
        <div>
        <Card sx={{ maxWidth: 345 }}>
          <CardMedia
            sx={{ height: 140 }}
            image={img}
            title={course}
          />
          <CardContent>
            <Typography gutterBottom variant="h5" component="div">
              {course}
            </Typography>
            <Typography variant="body2" color="text.secondary">
              {descr}
            </Typography>
          </CardContent>
          <CardActions>
            <Button onClick={TransitionsModal}>Open modal</Button>
            <Button size="small">Learn More</Button>
          </CardActions>
        </Card>
        <Modal
        open={isModalOpen}
        onClose={handleModalClose}
      >
        <div className="quiz-modal">
          <h3>JavaScript Quiz</h3>
          <img src="/assets/js-quiz.jpg" alt="JavaScript Quiz" />
          <p>Time estimate: 15 minutes</p>
          <p>Test your knowledge of JavaScript with this quiz.</p>
        </div>
      </Modal>
      </div>
      );;
}
