import React from 'react';
import { 
  styled, 
  Box, 
  Toolbar, 
  IconButton, 
  InputBase,
  Button
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import SearchIcon from '@mui/icons-material/Search';
import AddIcon from '@mui/icons-material/Add';

const HeadNav = styled(Box)(() => ({
  position: 'fixed',
  width: '100%',
  backgroundColor: 'transparent',
}));

const Search = styled('div')(() => ({
  position: 'relative',
  backgroundColor: 'rgba(0, 139, 240, 0.2)',
  borderRadius: '15px',
  transition: '0.5s',
  '&:hover': {
    backgroundColor: 'rgba(0, 139, 240, 0.6)',
  },
}));

const SearchIconWrapper = styled('div')(() => ({
  padding: '0px 5px 0px 5px',
  height: '100%',
  position: 'absolute',
  pointerEvents: 'none',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  color: '#ffffff',
}));

const StyledInputBase = styled(InputBase)(() => ({
  color: '#ffffff',
  paddingLeft: '30px',
}));

const ToggleButton = styled(MenuIcon)(() => ({
  color: '#ffffff',
}));

const AddQuizButton = styled(Button)(() => ({
  color: '#ffffff',
}));

const AddQuizWrap = styled(Box)(() => ({
  flexGrow: '1',
  display: 'flex',
  justifyContent: 'flex-end',
  marginRight: '1%',
}));

export default function PrimarySearchAppBar() {

  return (
    <Box sx={{ flexGrow: 1 }}>
      <HeadNav>
        <Toolbar>
          <IconButton
            size="large"
            edge="start"
            color="inherit"
            aria-label="open drawer"
            sx={{ mr: 2 }}
          >
            <ToggleButton/>
          </IconButton>
          <Search>
            <SearchIconWrapper>
              <SearchIcon />
            </SearchIconWrapper>
            <StyledInputBase
              placeholder="Searchâ€¦"
              inputProps={{ 'aria-label': 'search' }}
            />
          </Search>
          <AddQuizWrap>
            <AddQuizButton size="big" >
              <AddIcon/>
              Add quiz
            </AddQuizButton>
          </AddQuizWrap>  
        </Toolbar>
      </HeadNav>
    </Box>
  );
}
