import React, { useState } from 'react';
import { styled, useTheme } from '@mui/material/styles';
import { 
    Box,  
    Drawer,  
    CssBaseline,  
    Toolbar,  
    List,  
    Divider,  
    IconButton,  
    ListItem,  
    ListItemButton, 
    ListItemIcon, 
    ListItemText 
} from '@mui/material';
//icons-material не импортятся скопом, выдает ошибку
import MenuIcon from '@mui/icons-material/Menu';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import AddIcon from '@mui/icons-material/Add';
import FavoriteIcon from '@mui/icons-material/Favorite';

const DrawerHeader = styled('div')(() => ({
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'flex-end',
}));

const ToggleButton = styled('div')(() => ({
    color: '#ffffff',
    '&:hover': {
      color: '#FF7800',
    },
    cursor: 'pointer',
}));

export default function PersistentDrawerLeft() {
  const theme = useTheme();
  const [open, setOpen] = useState(false);

  const handleDrawerOpen = () => setOpen(true);
  const handleDrawerClose = () => setOpen(false);

  return (
    <Box sx={{ display: 'flex' }}>
      <CssBaseline />
      
        <Toolbar>
          <ToggleButton
            aria-label="open drawer"
            onClick={handleDrawerOpen}
            edge="start"
            sx={{ mr: 2, ...(open && { display: 'none' }) }}
          >
            <MenuIcon />
          </ToggleButton>
        </Toolbar>
      
      <Drawer
        sx={{
          width: 240,
          flexShrink: 0,
          '& .MuiDrawer-paper': {
            width: 240,
            boxSizing: 'border-box',
            backgroundColor: 'black',
          },
        }}
        variant="persistent"
        anchor="left"
        open={open}
      >
        <DrawerHeader>
          <IconButton onClick={handleDrawerClose}>
            {theme.direction === 'ltr' ? <ChevronLeftIcon sx={{ color: 'white' }}/> : <ChevronRightIcon sx={{ color: 'white' }}/>}
          </IconButton>
        </DrawerHeader>
        <Divider sx={{ backgroundColor: 'white' }}/>
        <List>
          {['Add product', 'Favourites'].map((text, index) => (
            <ListItem key={text} disablePadding>
              <ListItemButton>
                <ListItemIcon>
                  {index % 2 === 0 ? <AddIcon sx={{ color: 'white' }}/> : <FavoriteIcon sx={{ color: 'white' }}/>}
                </ListItemIcon>
                <ListItemText primary={text} sx={{ color: 'white' }}/>
              </ListItemButton>
            </ListItem>
          ))}
        </List>
      </Drawer>
    </Box>
  );
}
