import React, { useState } from "react";
import { 
    styled,
    Box,
    Typography,
    Button,
    CardContent,
    Avatar,
    Card,
} from "@mui/material";
import TransitionsModal from './Modals';


const CourceIcon = styled(Box)(() => ({
  width: '50px',
  height: '50px',
  borderRadius: '50%',
  display: 'block',
}));

const CardButtons = styled(Box)(() => ({
  display: 'flex',
  margin: '0 10px',
  justifyContent: 'center',
  alignItems: 'center',
}));


export default function QuizItem({ course, id, descr, img }) {

  const [isHover, setIsHover] = useState(false);

  const handleMouseEnter = () => setIsHover(true);
  const handleMouseLeave = () => setIsHover(false);

  const [modalOpen, setModalOpen] = useState(false);
  
  const handleModalOpen = () => {
    setModalOpen(true);
  }

  const boxStyle = {
     maxWidth: '380px',
     margin: '0 20px',
     fontSize: '30px',
     borderRadius: '20px',
     cursor: 'pointer',
     backgroundColor: isHover ? '#0407b3' : '#02049c',
     color: '#ffffff',
     padding: '15px',
     transition: '0.5s',
  };

    return (
      <div>
        <Card 
          sx={{ maxWidth: 345 }} 
          style={boxStyle} 
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
        >
          <CourceIcon>
            <Avatar
              alt={course}
              src={img}
              sx={{ width: 56, height: 56 }}
            />
          </CourceIcon>
          <CardContent>
            <Typography gutterBottom variant="h5" component="div">
              {course}
            </Typography>
            <Typography variant="body2" color="#ffffff">
              {descr}
            </Typography>
          </CardContent>
          <CardButtons>
            <Button size="small"  onClick={handleModalOpen}>Show More</Button>
            <Button size="small">Start QUIZ</Button>
          </CardButtons>
        </Card>
        <TransitionsModal open={modalOpen} handleClose={handleClose} />
      </div>
      );;
}
