import React, { useState } from "react";
import { 
    styled,
    Box,
    Typography,
    CardContent,
    Avatar,
    Card,
} from "@mui/material";

const CourceIcon = styled(Box)(() => ({
  width: '50px',
  height: '50px',
  borderRadius: '50%',
  display: 'block',
}));

const CardButtons = styled(Box)(() => ({
  display: 'flex',
  margin: '0 10px',
  justifyContent: 'center',
  alignItems: 'center',
}));


export default function QuizItem({ course, id, descr, img }) {

  const [isHover, setIsHover] = useState(false);

  const handleMouseEnter = () => {
     setIsHover(true);
  };

  const handleMouseLeave = () => {
     setIsHover(false);
  };

  const boxStyle = {
     maxWidth: '380px',
     margin: '0 20px',
     fontSize: '30px',
     borderRadius: '20px',
     cursor: 'pointer',
     backgroundColor: isHover ? '#6549D7' : '#3415B0',
     color: '#ffffff',
     padding: '15px',
     transition: '0.5s',
  };

  const ShowButton = styled(Box)(() => ({
    textAlign: 'center',
    fontSize: '16px',
    backgroundColor: isHover ? 'black' : '#0508ed',
    color: isHover ? 'black' : 'white',
  }));

  const StartButton = styled(Box)(() => ({
    textAlign: 'center',
    fontSize: '16px',
    color: isHover ? 'black' : 'white',
  }));

    return (
        <Card 
          sx={{ maxWidth: 345 }} 
          style={boxStyle} 
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
        >
          <CourceIcon>
            <Avatar
              alt={course}
              src={img}
              sx={{ width: 56, height: 56 }}
            />
          </CourceIcon>
          <CardContent>
            <Typography gutterBottom variant="h5" component="div">
              {course}
            </Typography>
            <Typography variant="body2" color="#ffffff">
              {descr}
            </Typography>
          </CardContent>
          <CardButtons>
            <ShowButton size="small">Show More</ShowButton>
            <StartButton size="small">Start QUIZ</StartButton>
          </CardButtons>
        </Card>
      );;
}
